#!/bin/bash\n\n# Configuration\nBACKUP_DIR="/path/to/backup/directory"\nDB_NAME="your_database_name"\nDB_USER="your_database_user"\nDB_HOST="your_database_host"\nDATE=$(date +"%Y-%m-%d")\nRETENTION_DAYS=7\n\n# Create backup\npg_dump -h $DB_HOST -U $DB_USER -F c -b -v -f "$BACKUP_DIR/$DB_NAME-$DATE.backup" $DB_NAME\n\n# Delete old backups\nfind $BACKUP_DIR -type f -name "$DB_NAME-*.backup" -mtime +$RETENTION_DAYS -exec rm -f {} \;\n\n# Optional: log the backup process\necho "Backup for database $DB_NAME completed on $DATE" >> $BACKUP_DIR/backup.log\n\n# Note: ensure that the script has execute permissions: chmod +x backup.sh\n# Also, you may need to set the PGPASSWORD environment variable or use a .pgpass file for authenticatio