#!/bin/bash\n\nREPO_PATH="/path/to/repo"\nBACKUP_DIR="/path/to/backup"\nDATE=$(date +%Y%m%d)\nHOTCOPY_DIR="$BACKUP_DIR/hotcopy_$DATE"\nFULL_DUMP="$BACKUP_DIR/full_dump_$DATE.svn"\nINCREMENTAL_DUMP="$BACKUP_DIR/incremental_dump_$DATE.svn"\nLAST_DUMP="$BACKUP_DIR/last_dump.svn"\n\n# Create hotcopy\nsvnadmin hotcopy $REPO_PATH $HOTCOPY_DIR\n\n# Create full dump (weekly)\nif [ "$(date +%u)" -eq 1 ]; then\n    svnadmin dump $REPO_PATH > $FULL_DUMP\n    cp $FULL_DUMP $LAST_DUMP\nelse\n    # Create incremental dump (daily)\n    svnadmin dump $REPO_PATH --incremental --revision $(svnlook youngest $REPO_PATH):HEAD > $INCREMENTAL_DUMP\n    cat $INCREMENTAL_DUMP >> $LAST_DUMP\nfi\n\n# Remove old backups (older than 30 days)\nfind $BACKUP_DIR -type d -name 'hotcopy_*' -mtime +30 -exec rm -rf {} \;\nfind $BACKUP_DIR -type f -name '*.svn' -mtime +30 -exec rm -f {} \;