#!/bin/bash\n\n   # Variables\n   SOURCE_DIR="/path/to/source/files/"\n   DEST_DIR="/path/to/destination/"\n   MEDIA_SERVER="user@media-server"\n   \n   # Rsync command to transfer files\n   rsync -avz --remove-source-files "$SOURCE_DIR" "$MEDIA_SERVER:$DEST_DIR"\n\n   if [ $? -eq 0 ]; then\n       echo "Files transferred successfully."\n   else\n       echo "An error occurred during file transfer."\n   fi