#!/bin/bash\n\n# Directory with the original files\ninput_directory="/path/to/input/directory"\n# Directory for the processed files (optional)\noutput_directory="/path/to/output/directory"\n\n# Create the output directory if it doesn't exist\nmkdir -p "$output_directory"\n\n# Loop through each file in the input directory\nfor file in "$input_directory"/*; do\n  # Extract the filename\n  filename=$(basename "$file")\n  # Set the output file path (use $file if you want to overwrite the original)\n  output_file="$output_directory/$filename"\n  \n  # Process the file with awk\n  awk '/\*\*\* Start / {flag=1; next} /\*\*\* Finish / {flag=0} flag {print}' "$file" > "$output_file"\ndone