#!/bin/bash\n\n# Define variables\nINPUT_FILE="your_document.docx"\nOUTPUT_FILE="secure_document.exe"\nPASSWORD="your_password"\n\n# Ensure 7-Zip (p7zip) is installed\nif ! command -v 7z &> /dev/null\nthen\n    echo "7z could not be found, please install it first."\n    exit\nfi\n\n# Create an encrypted, password-protected archive\n7z a -t7z -p"$PASSWORD" -mhe=on secure_document.7z "$INPUT_FILE"\n\n# Create a self-extracting executable\n7z sfx secure_document.7z -o"$OUTPUT_FILE"\n\n# Clean up\nrm secure_document.7z\n\necho "Self-extracting archive created: $OUTPUT_FILE"