#!/bin/bash\n\ninput_date="2008-06-01"\nday=$(date -j -f "%Y-%m-%d" "$input_date" +"%d")\nsuffix="th"\n\nif [[ $day == "01" || $day == "21" || $day == "31" ]]; then\n  suffix="st"\nelif [[ $day == "02" || $day == "22" ]]; then\n  suffix="nd"\nelif [[ $day == "03" || $day == "23" ]]; then\n  suffix="rd"\nfi\n\nformatted_date=$(date -j -f "%Y-%m-%d" "$input_date" +"%a %e")\nformatted_date="${formatted_date}${suffix} $(date -j -f "%Y-%m-%d" "$input_date" +"%B %Y")"\n\necho $formatted_date