#!/bin/bash\n\n# Path to the directory where modem lock files are stored\nLOCK_DIR="/var/lock"\n\n# List of available modems\nMODEMS=("/dev/ttyS0" "/dev/ttyS1" "/dev/ttyS2")\n\nfor MODEM in "${MODEMS[@]}"; do\n    LOCK_FILE="$LOCK_DIR/LCK..$(basename $MODEM)"\n    if [ ! -f "$LOCK_FILE" ]; then\n        echo "$MODEM"\n        exit 0\n    fi\ndone\n\necho "No modems available" >&2\nexit 1