#!/bin/bash\n\n# Generate the content\ncat /myspecialdir/foo > /httpd/foo.html\n\n# Print HTTP headers to prevent caching\nprintf "Content-Type: text/html\r\n"\nprintf "Pragma: no-cache\r\n"\nprintf "Cache-Control: no-store, no-cache, must-revalidate, max-age=0\r\n"\nprintf "Expires: 0\r\n"\nprintf "\r\n"  # End of headers\n\n# Print HTML content\necho "<HTML><HEAD><BODY>"\necho "<META HTTP-EQUIV=\"Expires\" CONTENT=\"0\">"\necho "<META HTTP-EQUIV=\"Pragma\" CONTENT=\"no-cache\">"\necho "<META HTTP-EQUIV=\"Cache-Control\" CONTENT=\"no-store, no-cache, must-revalidate, max-age=0\">"\necho "<META HTTP-EQUIV=\"Refresh\" CONTENT=\"1; URL=/foo.html\">"\necho "</BODY></HEAD></HTML>"