@echo off\nset service_name=YourServiceName\n\nrem Check if the service is installed\npowershell -Command "if ((Get-Service -Name '%service_name%' -ErrorAction SilentlyContinue) -ne $null) { exit 0 } else { exit 1 }"\nif %errorlevel% neq 0 (\n    echo Service %service_name% is not installed.\n    exit /b 1\n)\n\nrem Check if the service is running\npowershell -Command "if ((Get-Service -Name '%service_name%').Status -eq 'Running') { exit 0 } else { exit 1 }"\nif %errorlevel% neq 0 (\n    echo Service %service_name% is not running.\n    rem Optionally, start the service here\n    net start %service_name%\n) else (\n    echo Service %service_name% is already running.\n)