#!/bin/csh\n\n# Start a new group and execute commands within a subshell\nnewgrp name <<EOF\n(\n    # Source the required environment variables\n    source /stuff/morestuff/stuffiwant\n\n    # Execute the razor command and wait for it to finish\n    razor -c thread -f Keil1403 -g lib . -o\n\n    # Make all files executable\n    chmod +x *\n\n    # Execute the build script\n    ./rz_build*\n)\nEOF