#!/bin/bash\n\n# Function to check if a directory is an NFS mount point\nis_nfs_mount() {\n    local dir="$1"\n    if mount | grep -qs " on ${dir} type nfs"; then\n        echo "${dir} is an NFS mount point"\n    else\n        echo "${dir} is local"\n    fi\n}\n\n# Directories to check\ndirs=("localdir" "remotedir")\n\n# Loop through directories and check each one\nfor dir in "${dirs[@]}"; do\n    is_nfs_mount "$dir"\ndone