#!/bin/bash\n\nfollow_symlinks() {\n    local path=$1\n    while [ -L "$path" ]; do\n        target=$(readlink "$path")\n        echo "$path -> $target"\n        if [[ $target == /* ]]; then\n            path=$target\n        else\n            path=$(dirname "$path")/"$target"\n        fi\n    done\n    echo "$path"\n}\n\nif [ -z "$1" ]; then\n    echo "Usage: $0 <symlink>"\n    exit 1\nfi\n\nfollow_symlinks "$1"