#!/bin/bash\n\n# Define source and destination directories\nSOURCE_DIR="/path/to/source"\nDEST_DIR="/path/to/destination"\n\n# Execute rsync to perform differential backup\nrsync -av --delete --backup --backup-dir=/path/to/backup-dir $SOURCE_DIR $DEST_DIR\n\n# Schedule the script to run at intervals using cron\n# Example cron job to run every day at 2am\n# 0 2 * * * /path/to/backup_script.sh