#!/bin/bash\n\n# List of test cases\ntests=("Test1" "Test2" "Test3" "Test4" "Test5")\n\n# Number of machines\nnum_machines=3\n\n# Distribute tests\nfor ((i=0; i<${#tests[@]}; i++)); do\n    machine=$((i % num_machines))\n    echo "Running ${tests[$i]} on machine $machine"\n    ssh machine$machine "java -cp .:junit.jar org.junit.runner.JUnitCore ${tests[$i]}" &\ndone\n\nwait\necho "All tests finished."