# Install necessary dependencies\nsudo apt-get update\nsudo apt-get install build-essential libpcap-dev libnfnetlink-dev libnetfilter-conntrack-dev\n\n# Clone the MiniUPnPd repository\ngit clone https://github.com/miniupnp/miniupnp.git\ncd miniupnp/miniupnpd\n\n# Build and install MiniUPnPd\nmake\nsudo make install\n\n# Copy the sample configuration file and edit it as needed\nsudo cp upnpd.conf /etc/miniupnpd/\nsudo nano /etc/miniupnpd/upnpd.conf\n\n# Start MiniUPnPd\nsudo miniupnpd -d -f /etc/miniupnpd/upnpd.conf