# .htaccess file in your root directory\nRewriteEngine On\n\n# Redirect all requests for assets to the subdomain\nRewriteCond %{HTTP_HOST} ^www\.mysitename\.com$ [NC]\nRewriteRule ^assets/css/images/(.*)$ http://images.mysitename.com/$1 [R=301,L]\nRewriteRule ^assets/js/(.*)$ http://js.mysitename.com/$1 [R=301,L]\nRewriteRule ^assets/css/(.*)$ http://css.mysitename.com/$1 [R=301,L]