#!/bin/bash\n\n# Define the test executable\nTEST_EXECUTABLE="./my_test_suite"\n\n# Run the test suite with Valgrind's memcheck tool\nvalgrind --leak-check=full --track-origins=yes --log-file=valgrind.log $TEST_EXECUTABLE\n\n# Check the log file for errors\nif grep -q "ERROR SUMMARY: 0 errors" valgrind.log; then\n    echo "No memory errors detected."\n    exit 0\nelse\n    echo "Memory errors detected! Check valgrind.log for details."\n    exit 1\nfi