awk '\nBEGIN {\n    FS = " "\n    # Load the modifier file into an array\n    while (getline < "modifier.file" > 0) {\n        id_number = $1\n        amount_date = $2 " " $3\n        modifiers[id_number] = amount_date\n    }\n    close("modifier.file")\n}\n\n{\n    # Extract the id_number from the current line\n    id_number = substr($0, 501, 20) # Adjust indices according to your data\n\n    if (id_number in modifiers) {\n        # Modify the line\n        $0 = substr($0, 1, 650) modifiers[id_number] substr($0, 671)\n    }\n\n    print\n}' data.file > modified_data.file