#!/bin/bash\n\n# Directory containing the files\nDIRECTORY="/path/to/your/files"\n\n# Navigate to the directory\ncd "$DIRECTORY" || exit\n\n# Loop through each file matching the pattern\nfor file in Prefix_*.tex; do\n  # Extract the date part ddmmyy\n  date_part=$(echo "$file" | sed -n 's/^Prefix_\([0-9]\{2\}\)\([0-9]\{2\}\)\([0-9]\{2\}\).tex$/\1\2\3/p')\n  \n  # If date_part is not empty, proceed with renaming\n  if [ -n "$date_part" ]; then\n    # Form the new date part yymmdd\n    new_date_part="${date_part:4:2}${date_part:2:2}${date_part:0:2}"\n    \n    # Form the new file name\n    new_file="Prefix_${new_date_part}.tex"\n    \n    # Rename the file\n    mv "$file" "$new_file"\n  fi\ndone