#!/bin/bash\n\n# Save directory permissions\nsave_permissions() {\n    find . -type d -exec stat -c '%a %n' {} \; > permissions.txt\n    git add permissions.txt\n    git commit -m "Save directory permissions"\n}\n\n# Apply directory permissions\napply_permissions() {\n    while IFS= read -r line; do\n        perm=$(echo $line | cut -d' ' -f1)\n        dir=$(echo $line | cut -d' ' -f2-)\n        chmod $perm "$dir"\n    done < permissions.txt\n}\n\n# Example usage:\n# Save permissions before commit\nsave_permissions\n\n# Apply permissions after checkout\napply_permissions