#!/bin/bash\n\nINDEXING_SERVER="indexing_server_ip"\nLOAD_THRESHOLD=4  # Adjust based on your desired threshold\n\nLOAD=$( /usr/lib/nagios/plugins/check_nrpe -H $INDEXING_SERVER -c check_load )\n\n# Extract the 1-minute load average from the output\nLOAD_1MIN=$(echo $LOAD | awk '{print $4}' | sed 's/,//')\n\nif (( $(echo "$LOAD_1MIN < $LOAD_THRESHOLD" | bc -l) )); then\n    echo "Indexing server load is below threshold. Proceeding with processing."\n    # Add your processing logic here\nelse\n    echo "Indexing server load is above threshold. Slowing down processing."\n    # Add your throttling logic here\nfi