#!/bin/bash\n\n      DB_USER="youruser"\n      DB_PASS="yourpassword"\n      DB_NAME="yourdb"\n      VERSION_TABLE="schema_version"\n\n      # Function to get the current version from the database\n      get_current_version() {\n          mysql -u "$DB_USER" -p"$DB_PASS" -D "$DB_NAME" -Bse "SELECT version FROM $VERSION_TABLE ORDER BY version DESC LIMIT 1;"\n      }\n\n      # Function to apply a SQL script\n      apply_script() {\n          local script="$1"\n          echo "Applying $script"\n          mysql -u "$DB_USER" -p"$DB_PASS" -D "$DB_NAME" < "$script"\n          local new_version=$(basename "$script" | sed 's/[^0-9]*//g')\n          mysql -u "$DB_USER" -p"$DB_PASS" -D "$DB_NAME" -Bse "INSERT INTO $VERSION_TABLE (version) VALUES ('$new_version');"\n      }\n\n      # Main logic\n      current_version=$(get_current_version)\n      for script in $(ls -1v migrations/*.sql); do\n          script_version=$(basename "$script" | sed 's/[^0-9]*//g')\n          if [ "$script_version" -gt "$current_version" ]; then\n              apply_script "$script"\n          fi\n      done