# On Primary Server\n# Set up MySQL replication\necho "\n[mysqld]\nserver-id=1\nlog-bin=mysql-bin\n" >> /etc/mysql/my.cnf\n\n# Set up rsync for file synchronization\necho "*/10 * * * * rsync -avz /var/www/ user@secondary:/var/www/" | crontab -\n\n# On Secondary Server\n# Set up MySQL as a slave\necho "\n[mysqld]\nserver-id=2\nreplicate-do-db=mydb\n" >> /etc/mysql/my.cnf\n\n# Start replication process\nmysql> CHANGE MASTER TO MASTER_HOST='primary', MASTER_USER='replication_user', MASTER_PASSWORD='password', MASTER_LOG_FILE='mysql-bin.000001', MASTER_LOG_POS=4;\nmysql> START SLAVE;\n\n# Set up rsync to pull files from primary server\necho "*/10 * * * * rsync -avz user@primary:/var/www/ /var/www/" | crontab -