#!/bin/bash\n\n   # User-specific certificate information\n   USER_CERT="$HOME/user.p12"\n   USER_CERT_PASSWORD_FILE="$HOME/password.txt"\n   CA_TRUSTSTORE="/central/ca.jks"\n\n   # Read the password from the password file\n   if [ -f "$USER_CERT_PASSWORD_FILE" ]; then\n       USER_CERT_PASSWORD=$(cat "$USER_CERT_PASSWORD_FILE")\n   else\n       echo "Password file not found: $USER_CERT_PASSWORD_FILE"\n       exit 1\n   fi\n\n   # Set JVM system properties for SSL\n   JVM_OPTS="-Djavax.net.ssl.keyStore=$USER_CERT"\n   JVM_OPTS="$JVM_OPTS -Djavax.net.ssl.keyStoreType=PKCS12"\n   JVM_OPTS="$JVM_OPTS -Djavax.net.ssl.keyStorePassword=$USER_CERT_PASSWORD"\n   JVM_OPTS="$JVM_OPTS -Djavax.net.ssl.trustStore=$CA_TRUSTSTORE"\n   JVM_OPTS="$JVM_OPTS -Djavax.net.ssl.trustStoreType=JKS"\n\n   # Launch Eclipse with the custom JVM options\n   /central/eclipse_3.4.0/eclipse $JVM_OPTS "$@"