# Add routing tables\necho "10 custom1" >> /etc/iproute2/rt_tables\necho "20 custom2" >> /etc/iproute2/rt_tables\n\n# Configure routes for each table\nip route add 172.17.32.0/24 dev eth0 table custom1\nip route add 172.17.32.0/24 dev eth1 table custom2\n\n# Add rules to use the tables based on source IP\nip rule add from 172.17.32.10 table custom1\nip rule add from 172.17.32.11 table custom2