#!/bin/bash\n\n# Directory where all the SVN repositories are located\nREPO_DIR="/path/to/repositories"\n\n# Directory where the backups will be stored\nBACKUP_DIR="/path/to/backup"\n\n# Create backup directory if it doesn't exist\nmkdir -p "$BACKUP_DIR"\n\n# Loop through each repository in the repository directory\nfor REPO in "$REPO_DIR"/*; do\n    if [ -d "$REPO" ]; then\n        REPO_NAME=$(basename "$REPO")\n        svnadmin dump "$REPO" > "$BACKUP_DIR/$REPO_NAME.dump"\n        echo "Backed up $REPO_NAME"\n    fi\ndone\n\necho "All repositories have been backed up."