# Suppose you have downloaded and extracted the testing version into /path/to/eclipse-motif\nMOTIF_ECLIPSE_DIR="/path/to/eclipse-motif"\nDEV_ECLIPSE_DIR="/path/to/your/dev/eclipse"\n\n# Copy Motif plugins to your development Eclipse environment\ncp -r $MOTIF_ECLIPSE_DIR/plugins/org.eclipse.swt.gtk.linux.x86_64_motif* $DEV_ECLIPSE_DIR/plugins/\ncp -r $MOTIF_ECLIPSE_DIR/features/org.eclipse.rcp.motif* $DEV_ECLIPSE_DIR/features/\n\n# Update dev Eclipse configuration if necessary\necho "osgi.bundles=reference\:file\:plugins/org.eclipse.swt.gtk.linux.x86_64_motif_*.jar" >> $DEV_ECLIPSE_DIR/configuration/config.ini\n\n# Now open your Eclipse IDE and proceed with the export wizard