#!/bin/bash\n\n# Usage: change_permissions.sh <command> <file/directory> <owner> <permissions>\n# <command>: chown or chmod\n# <file/directory>: path to the file or directory\n# <owner>: new owner (for chown) or empty string (for chmod)\n# <permissions>: new permissions (for chmod) or empty string (for chown)\n\nCOMMAND=$1\nTARGET=$2\nOWNER=$3\nPERMISSIONS=$4\n\nif [ "$COMMAND" = "chown" ]; then\n    chown $OWNER $TARGET\nelif [ "$COMMAND" = "chmod" ]; then\n    chmod $PERMISSIONS $TARGET\nelse\n    echo "Invalid command"\n    exit 1\nfi