# Install necessary packages\n     sudo apt-get install iptables-persistent\n\n     # Enable IP forwarding\n     sudo sysctl -w net.ipv4.ip_forward=1\n     echo "net.ipv4.ip_forward=1" | sudo tee -a /etc/sysctl.conf\n\n     # Configure iptables rules\n     sudo iptables -t nat -A PREROUTING -d x.x.x.123 -p tcp --dport 21 -j DNAT --to-destination 192.168.1.2:21\n     sudo iptables -t nat -A PREROUTING -d x.x.x.123 -p tcp --dport 80 -j DNAT --to-destination 192.168.1.3:80\n     sudo iptables -t nat -A PREROUTING -d x.x.x.124 -p tcp --dport 80 -j DNAT --to-destination 192.168.1.4:80\n     sudo iptables -t nat -A PREROUTING -d x.x.x.123 -p tcp --dport 5000 -j DNAT --to-destination 192.168.1.5:5000\n\n     # Save iptables rules\n     sudo netfilter-persistent save