# Flash DD-WRT firmware on your router\n     # Configure Port Forwarding for multiple IPs\n     # Example configuration for DD-WRT\n     iptables -t nat -A PREROUTING -i vlan2 -d x.x.x.123 -p tcp --dport 21 -j DNAT --to 192.168.1.2:21\n     iptables -t nat -A PREROUTING -i vlan2 -d x.x.x.123 -p tcp --dport 80 -j DNAT --to 192.168.1.3:80\n     iptables -t nat -A PREROUTING -i vlan2 -d x.x.x.124 -p tcp --dport 80 -j DNAT --to 192.168.1.4:80\n     iptables -t nat -A PREROUTING -i vlan2 -d x.x.x.123 -p tcp --dport 5000 -j DNAT --to 192.168.1.5:5000