using System;\nusing System.Data;\nusing System.Windows.Forms;\nusing System.Xml;\n\nnamespace XmlToListView\n{\n    public partial class MainForm : Form\n    {\n        public MainForm()\n        {\n            InitializeComponent();\n            LoadXmlToListView("path_to_your_xml_file.xml");\n        }\n\n        private void LoadXmlToListView(string xmlFilePath)\n        {\n            DataSet dataSet = new DataSet();\n            dataSet.ReadXml(xmlFilePath);\n\n            DataTable dataTable = dataSet.Tables["Song"];\n            \n            // Assuming your ListView is named listViewSongs and already added to your form\n            listViewSongs.View = View.Details;\n            listViewSongs.FullRowSelect = true;\n            listViewSongs.Columns.Add("Name", 150);\n            listViewSongs.Columns.Add("Artist", 150);\n            listViewSongs.Columns.Add("Genre", 150);\n\n            foreach (DataRow row in dataTable.Rows)\n            {\n                ListViewItem item = new ListViewItem(row["Name"].ToString());\n                item.SubItems.Add(row["Artist"].ToString());\n                item.SubItems.Add(row["Genre"].ToString());\n                listViewSongs.Items.Add(item);\n            }\n        }\n    }\n}