public class BaseOrderMap : ClassMap<BaseOrder>\n{\n    public BaseOrderMap()\n    {\n        Id(x => x.Id).GeneratedBy.Identity();\n        Map(x => x.Property1);\n        Map(x => x.Property2);\n        Map(x => x.Property3);\n        Map(x => x.Reference);\n        DiscriminateSubClassesOnColumn("Status");\n    }\n}\n\npublic class InCompleteOrderMap : SubclassMap<InCompleteOrder>\n{\n    public InCompleteOrderMap()\n    {\n        DiscriminatorValue((int)Status.InComplete);\n        Table("InCompleOrders");\n    }\n}\n\npublic class OrderMap : SubclassMap<Order>\n{\n    public OrderMap()\n    {\n        DiscriminatorValue((int)Status.Complete);\n        Table("CompleteOrders");\n        Map(x => x.DeliveredOn);\n        Map(x => x.PaidOn);\n    }\n}