using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        string visualStudioPath = @"C:\Program Files (x86)\Microsoft Visual Studio\2019\Community\Common7\IDE\devenv.exe"; // Update the path as needed\n        string solutionPath = @"C:\Path\To\Your\Solution.sln"; // Update the path to your solution\n\n        ProcessStartInfo startInfo = new ProcessStartInfo\n        {\n            FileName = visualStudioPath,\n            Arguments = "\"" + solutionPath + "\"",\n            UseShellExecute = false\n        };\n\n        try\n        {\n            Process process = Process.Start(startInfo);\n            if (process != null)\n            {\n                Console.WriteLine("New instance of Visual Studio started.");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error starting Visual Studio: " + ex.Message);\n        }\n    }\n}