protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        SwapTextBoxesForLabels(this);\n    }\n}\n\nprivate void SwapTextBoxesForLabels(Control parent)\n{\n    foreach (Control control in parent.Controls)\n    {\n        if (control is TextBox textBox && !textBox.Enabled)\n        {\n            Label label = new Label\n            {\n                ID = "Label_" + textBox.ID,\n                Text = textBox.Text,\n                CssClass = textBox.CssClass\n            };\n            \n            int index = parent.Controls.IndexOf(textBox);\n            parent.Controls.RemoveAt(index);\n            parent.Controls.AddAt(index, label);\n        }\n        else if (control.HasControls())\n        {\n            SwapTextBoxesForLabels(control);\n        }\n    }\n}