// Assume you have a method to get search results from the database\npublic List<SearchResult> GetSearchResults(string query)\n{\n    // Caching the results in a session or memory\n    List<SearchResult> results;\n    if (HttpContext.Current.Session["SearchResults"] == null)\n    {\n        results = FetchResultsFromDatabase(query);\n        HttpContext.Current.Session["SearchResults"] = results;\n    }\n    else\n    {\n        results = (List<SearchResult>)HttpContext.Current.Session["SearchResults"];\n    }\n    return results;\n}