using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class Order\n{\n    public int CustomerId { get; set; }\n    public int ProductId { get; set; }\n    public int ProductCount { get; set; }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        List<Order> orders = new List<Order>\n        {\n            new Order { CustomerId = 1, ProductId = 1, ProductCount = 10 },\n            new Order { CustomerId = 1, ProductId = 1, ProductCount = 5 },\n            new Order { CustomerId = 1, ProductId = 2, ProductCount = 7 },\n            new Order { CustomerId = 2, ProductId = 1, ProductCount = 3 },\n            new Order { CustomerId = 2, ProductId = 2, ProductCount = 8 }\n        };\n\n        var groupedOrders = orders\n            .GroupBy(o => new { o.CustomerId, o.ProductId })\n            .Select(g => new\n            {\n                CustomerId = g.Key.CustomerId,\n                ProductId = g.Key.ProductId,\n                TotalProductCount = g.Sum(o => o.ProductCount)\n            });\n\n        foreach (var group in groupedOrders)\n        {\n            Console.WriteLine($"CustomerId: {group.CustomerId}, ProductId: {group.ProductId}, TotalProductCount: {group.TotalProductCount}");\n        }\n    }\n}