using (var context = new ApplicationDbContext())\n{\n    // Create\n    var user = new User { Name = "John Doe" };\n    context.Users.Add(user);\n    context.SaveChanges();\n\n    // Read\n    var users = context.Users.ToList();\n\n    // Update\n    user.Name = "Jane Doe";\n    context.SaveChanges();\n\n    // Delete\n    context.Users.Remove(user);\n    context.SaveChanges();\n}