var result = \n    from a in aTable\n    where \n        (from b in bTable\n         where \n             (from c in cTable\n              where \n                  (from d in dTable\n                   where d.dField == "a guid"\n                   select d.cTableId)\n                  .Contains(c.cTableId)\n              select c.bTableId)\n             .Contains(b.bTableId)\n         select b.aTableId)\n        .Contains(a.aTableId)\n    select a;