// foo and bar have some identical elements (given a case-insensitive match)\nList<string> foo = GetFoo();\nList<string> bar = GetBar();\n\n// Create HashSet for bar with case-insensitive comparison\nHashSet<string> barSet = new HashSet<string>(bar, StringComparer.InvariantCultureIgnoreCase);\n\n// Filter foo using barSet\nfoo = foo.Where(x => barSet.Contains(x)).ToList();\n\n// Create HashSet for foo with case-insensitive comparison\nHashSet<string> fooSet = new HashSet<string>(foo, StringComparer.InvariantCultureIgnoreCase);\n\n// Filter bar using fooSet\nbar = bar.Where(x => fooSet.Contains(x)).ToList();