using System;\nusing System.Reflection;\n\npublic class Example\n{\n    public double[][][] d;\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        Type type = typeof(Example);\n        FieldInfo fieldInfo = type.GetField("d");\n        string descriptor = GetDescriptor(fieldInfo.FieldType);\n        Console.WriteLine(descriptor);\n    }\n\n    public static string GetDescriptor(Type type)\n    {\n        if (type.IsArray)\n        {\n            int rank = type.GetArrayRank();\n            string elementType = GetDescriptor(type.GetElementType());\n            return new string('[', rank) + elementType;\n        }\n        else if (type == typeof(double))\n        {\n            return "D";\n        }\n        // Add more type mappings as needed\n        return type.Name;\n    }\n}