SqlQuery sq = new Select(\n    Tables.TableOne + "." + TableOne.Columns.MemberId + " AS MemberId",\n    Tables.TableTwo + "." + TableTwo.Columns.Name + " AS MemberName",\n    Tables.TableOne + "." + TableOne.Columns.ExpiryOn + " AS MembershipExpiresOn",\n    Tables.TableFour + "." + TableFour.Columns.Name + " AS Country")\n    .From(Tables.TableOne)\n    .InnerJoin(Tables.TableTwo, TableOne.Columns.SomeForeignKey, TableTwo.Columns.PrimaryKey) // Adjust the join condition as per your schema\n    .InnerJoin(Tables.TableThree, TableOne.Columns.AnotherForeignKey, TableThree.Columns.PrimaryKey) // Adjust the join condition as per your schema\n    .InnerJoin(Tables.TableFour, TableFour.Columns.CountryCode, TableThree.Columns.CountryOfBirth)\n    .Where(Tables.TableTwo + "." + TableTwo.Columns.Name).Like("A%");\n\n// Execute the query\nvar result = sq.Execute();