public void DownloadReport(HttpResponse response, string content, string fileName)\n{\n    // Set the content type and the file name\n    response.ContentType = "application/octet-stream";\n    response.AddHeader("Content-Disposition", $"attachment; filename={fileName}");\n    \n    // Write the content directly to the response stream\n    using (var writer = new StreamWriter(response.OutputStream))\n    {\n        writer.Write(content);\n    }\n    \n    // End the response to ensure the content is sent to the client\n    response.End();\n}