using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        string categoryName = "Web Service";\n        string counterName = "Current Connections";\n        string instanceName = "_Total"; // Use the instance name that corresponds to your IIS site\n        string serverName = "RemoteServerName"; // Replace with the name of your remote server\n\n        try\n        {\n            PerformanceCounter perfCounter = new PerformanceCounter(categoryName, counterName, instanceName, serverName);\n            \n            while (true)\n            {\n                float requestsPerSec = perfCounter.NextValue();\n                Console.WriteLine($"Current Connections: {requestsPerSec}");\n                \n                if (requestsPerSec > 0)\n                {\n                    Console.WriteLine("The IIS site is receiving requests.");\n                }\n                else\n                {\n                    Console.WriteLine("The IIS site is not receiving requests.");\n                }\n\n                // Wait for 1 second before checking again\n                System.Threading.Thread.Sleep(1000);\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n    }\n}