using System;\nusing System.Text;\nusing System.Web;\n\npublic class BasicAuthModule : IHttpModule\n{\n    public void Init(HttpApplication context)\n    {\n        context.AuthenticateRequest += new EventHandler(OnAuthenticateRequest);\n    }\n\n    public void Dispose() { }\n\n    private void OnAuthenticateRequest(object sender, EventArgs e)\n    {\n        HttpApplication app = (HttpApplication)sender;\n        HttpRequest request = app.Context.Request;\n        HttpResponse response = app.Context.Response;\n\n        if (request.Path.EndsWith("/elmah.axd/rss", StringComparison.OrdinalIgnoreCase))\n        {\n            string authHeader = request.Headers["Authorization"];\n            if (!string.IsNullOrEmpty(authHeader))\n            {\n                string[] credentials = Encoding.ASCII.GetString(Convert.FromBase64String(authHeader.Substring(6))).Split(':');\n                string username = credentials[0];\n                string password = credentials[1];\n\n                if (ValidateUser(username, password))\n                {\n                    return;\n                }\n            }\n\n            response.StatusCode = 401;\n            response.StatusDescription = "Unauthorized";\n            response.AddHeader("WWW-Authenticate", "Basic realm=\"ELMAH\"");\n            response.End();\n        }\n    }\n\n    private bool ValidateUser(string username, string password)\n    {\n        // Replace with your logic to validate the username and password\n        return username == "admin" && password == "password";\n    }\n}