using System;\nusing System.Collections.Specialized;\nusing System.Web;\nusing System.Web.Hosting;\nusing System.Web.UI;\n\npublic class CustomSiteMapProvider : StaticSiteMapProvider\n{\n    private SiteMapNode rootNode;\n\n    public override SiteMapNode BuildSiteMap()\n    {\n        lock (this)\n        {\n            if (rootNode == null)\n            {\n                Clear();\n                rootNode = new SiteMapNode(this, "root", "~/", "Home");\n                AddNode(rootNode);\n\n                // Add dynamic article nodes\n                for (int i = 1; i <= 10000; i++)\n                {\n                    string url = $"~/articles/{i}/article-title";\n                    SiteMapNode articleNode = new SiteMapNode(this, "article" + i, url, "Article " + i);\n                    AddNode(articleNode, rootNode);\n                }\n            }\n            return rootNode;\n        }\n    }\n\n    protected override SiteMapNode GetRootNodeCore()\n    {\n        return BuildSiteMap();\n    }\n\n    public override bool IsAccessibleToUser(HttpContext context, SiteMapNode node)\n    {\n        // Implement your logic to control access to the nodes\n        return true;\n    }\n}