using System;\nusing System.IO;\nusing System.Net;\nusing System.Text;\nusing System.Web.Script.Serialization;\nusing System.Windows.Forms;\n\npublic class Person\n{\n    public string firstName { get; set; }\n    public string lastName { get; set; }\n    public string email { get; set; }\n    public string deviceUUID { get; set; }\n}\n\npublic partial class Form1 : Form\n{\n    public Form1()\n    {\n        InitializeComponent();\n    }\n\n    private void button1_Click(object sender, EventArgs e)\n    {\n        Person p = new Person();\n        p.firstName = "Bill";\n        p.lastName = "Gates";\n        p.email = "asdf@hotmail.com";\n        p.deviceUUID = "abcdefghijklmnopqrstuvwxyz";\n\n        JavaScriptSerializer serializer = new JavaScriptSerializer();\n        string json = serializer.Serialize(p);\n\n        string postData = $"firstName={Uri.EscapeDataString(p.firstName)}&lastName={Uri.EscapeDataString(p.lastName)}&email={Uri.EscapeDataString(p.email)}&deviceUUID={Uri.EscapeDataString(p.deviceUUID)}";\n\n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create("http://www.davemateer.com/ig/genius/newuser.php");\n        request.Method = "POST";\n        request.ContentType = "application/x-www-form-urlencoded";\n\n        byte[] byteArray = Encoding.UTF8.GetBytes(postData);\n        request.ContentLength = byteArray.Length;\n\n        using (Stream dataStream = request.GetRequestStream())\n        {\n            dataStream.Write(byteArray, 0, byteArray.Length);\n        }\n\n        using (WebResponse response = request.GetResponse())\n        {\n            using (Stream dataStream = response.GetResponseStream())\n            {\n                using (StreamReader reader = new StreamReader(dataStream))\n                {\n                    string responseFromServer = reader.ReadToEnd();\n                    textBox4.Text = responseFromServer;\n                }\n            }\n        }\n    }\n}