using System;\nusing System.Collections;\n\nclass Program\n{\n    static void Main()\n    {\n        BitArray bitArray = new BitArray(new bool[] { false, false, true });\n\n        bool containsTrue = Contains(bitArray, true);\n        bool containsFalse = Contains(bitArray, false);\n\n        Console.WriteLine("Contains True: " + containsTrue);\n        Console.WriteLine("Contains False: " + containsFalse);\n    }\n\n    static bool Contains(BitArray bitArray, bool value)\n    {\n        foreach (bool bit in bitArray)\n        {\n            if (bit == value)\n            {\n                return true;\n            }\n        }\n        return false;\n    }\n}