using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    // Constants for window styles\n    const int GWL_STYLE = -16;\n    const int WS_BORDER = 0x00800000;\n    const int WS_DLGFRAME = 0x00400000;\n    const int WS_CAPTION = 0x00C00000; // WS_BORDER | WS_DLGFRAME\n\n    // Import necessary functions from user32.dll\n    [DllImport("user32.dll", SetLastError = true)]\n    static extern int SetWindowLong(IntPtr hWnd, int nIndex, int dwNewLong);\n\n    [DllImport("user32.dll", SetLastError = true)]\n    static extern int GetWindowLong(IntPtr hWnd, int nIndex);\n\n    [DllImport("user32.dll", SetLastError = true)]\n    static extern bool SetWindowPos(IntPtr hWnd, IntPtr hWndInsertAfter, int X, int Y, int cx, int cy, uint uFlags);\n\n    const int SWP_NOSIZE = 0x0001;\n    const int SWP_NOMOVE = 0x0002;\n    const int SWP_NOZORDER = 0x0004;\n    const int SWP_FRAMECHANGED = 0x0020;\n\n    static void Main(string[] args)\n    {\n        IntPtr hWnd = /* your window handle here */;\n        int style = GetWindowLong(hWnd, GWL_STYLE);\n\n        // Remove the border and title bar\n        style &= ~WS_CAPTION;\n\n        SetWindowLong(hWnd, GWL_STYLE, style);\n        SetWindowPos(hWnd, IntPtr.Zero, 0, 0, 0, 0, SWP_NOSIZE | SWP_NOMOVE | SWP_NOZORDER | SWP_FRAMECHANGED);\n    }\n}