using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string xmlContent = @"\n        <?xml version='1.0' encoding='UTF-8'?>\n        <!-- Copyright (c) 2001-2009, all rights reserved -->\n        <root xmlns='http://example.com/namespace'>\n            <entry>\n                <id>2040-1247062136726-5485131</id>\n                <status>1</status>\n                <type>Normal</type>\n                <!-- Other elements -->\n            </entry>\n            <!-- More entries -->\n        </root>";\n\n        // Load the XML document\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.LoadXml(xmlContent);\n\n        // Remove the namespace\n        RemoveNamespace(xmlDoc);\n\n        // Use XPath to navigate the XML\n        XmlNodeList entries = xmlDoc.SelectNodes("//entry");\n        foreach (XmlNode entry in entries)\n        {\n            string id = entry.SelectSingleNode("id")?.InnerText;\n            string status = entry.SelectSingleNode("status")?.InnerText;\n            string type = entry.SelectSingleNode("type")?.InnerText;\n\n            Console.WriteLine($"ID: {id}, Status: {status}, Type: {type}");\n        }\n    }\n\n    static void RemoveNamespace(XmlDocument xmlDoc)\n    {\n        if (xmlDoc.DocumentElement != null)\n        {\n            foreach (XmlNode node in xmlDoc.DocumentElement.ChildNodes)\n            {\n                RemoveNamespace(node);\n            }\n        }\n    }\n\n    static void RemoveNamespace(XmlNode node)\n    {\n        if (node.NodeType == XmlNodeType.Element)\n        {\n            XmlElement elem = (XmlElement)node;\n            if (elem.HasAttributes)\n            {\n                elem.RemoveAttribute("xmlns");\n            }\n        }\n\n        foreach (XmlNode childNode in node.ChildNodes)\n        {\n            RemoveNamespace(childNode);\n        }\n    }\n}