// Adding the behavior to a client\n   var client = new YourServiceClient();\n   client.Endpoint.EndpointBehaviors.Add(new ExtendedPropertiesBehavior());\n\n   // Adding the behavior to a service host\n   var host = new ServiceHost(typeof(YourService));\n   foreach (var endpoint in host.Description.Endpoints)\n   {\n       endpoint.Behaviors.Add(new ExtendedPropertiesBehavior());\n   }\n   host.Open();